<template>
  <v-card class="sidebar-card">
    <div class="table-title">
      <div class="text-subtitle-1">Manage Your Products</div>
      <v-btn color="warning" size="small" @click="openCreateDialog">Create</v-btn>
    </div>

    <v-skeleton-loader v-if="loading" type="list-item, list-item, list-item" />

    <div v-else-if="!products.length" class="py-6 text-center">
      <div class="text-body-2 mb-2">No products yet</div>
      <v-btn color="primary" size="small" @click="openCreateDialog">Create Product</v-btn>
    </div>

    <div v-else class="stack">
      <v-card
        v-for="item in products"
        :key="item.id"
        class="prod-row"
        variant="tonal"
        v-ripple
        hover
        role="button"
        tabindex="0"
        :aria-label="`Open product ${item.name}`"
        @click="goToProduct(item)"
        @keydown.enter.prevent="goToProduct(item)"
        @keydown.space.prevent="goToProduct(item)"
      >
        <v-avatar size="56" rounded="lg" class="elevation-1">
          <!-- tiny placeholder only -->
          <v-img :src="item.image || placeholder" alt="product image" cover />
        </v-avatar>

        <div class="flex-1">
          <div class="text-subtitle-2 truncate">{{ item.name }}</div>
          <div class="text-caption text-medium-emphasis">
            {{ formatPrice(item.price) }}
          </div>
        </div>
      </v-card>
    </div>
  </v-card>

  <!-- Create dialog only -->
  <v-dialog v-model="dialog.open" max-width="520">
    <v-card>
      <v-card-title class="text-h6">Create Product</v-card-title>
      <v-card-text>
        <v-form ref="formRef" v-model="formIsValid">
          <v-text-field v-model="form.name" label="Name" class="mb-3" autofocus />
          <v-text-field v-model.number="form.price" label="Price" type="number" class="mb-3" prefix="$" />
          <v-text-field v-model="form.image" label="Image URL (optional)" class="mb-3" placeholder="[image href]" />
          <v-text-field v-model="form.sku" label="SKU (optional)" class="mb-1" />
        </v-form>
      </v-card-text>
      <v-card-actions class="justify-end">
        <v-btn variant="text" @click="closeDialog">Cancel</v-btn>
        <v-btn color="primary" :loading="saving" :disabled="!formIsValid" @click="saveProduct">
          Create
        </v-btn>
      </v-card-actions>
    </v-card>
  </v-dialog>
</template>

<script setup>
import { ref, onMounted, inject } from 'vue'
import { useRouter } from 'vue-router'
import { useUserStore } from '@/store/user'
import { storeToRefs } from 'pinia'

const router = useRouter()
const userStore = useUserStore()
const { user } = storeToRefs(userStore)
const { show } = inject('toast') ?? {}

const loading = ref(false)
const saving  = ref(false)
const products = ref([])

const dialog = ref({ open: false, mode: 'create', editingId: null })

const formRef = ref(null)
const formIsValid = ref(false)
const form = ref({ name: '', price: null, image: '', sku: '' })

// tiny, fast placeholder
const placeholder = 'https://dummyimage.com/160x160/e0e0e0/9e9e9e.png&text=Image'

onMounted(async () => {
  await loadProducts()
})

async function loadProducts () {
  try {
    loading.value = true
    await userStore.getSession()

    // super light mock data — no base64 images
    const mocked = [
      { id: 'p_1001', name: 'Evergreen Tee', price: 24.99, sku: 'TEE-EVG-001', image: 'data:image/jpeg;base64,',
 },
      { id: 'p_1002', name: 'Trail Runner Pack', price: 89.0,  sku: 'PACK-TRAIL-002', image: 'data:image/jpeg;base64,',
 },
      { id: 'p_1003', name: 'Insulated Bottle 32oz', price: 29.5, sku: 'BOT-32-003', image: 'data:image/jpeg;base64,',
 },
      { id: 'p_1004', name: 'Canvas Duffel', price: 119.99, sku: 'DUF-CAN-004', image: 'data:image/jpeg;base64,',
 },
      { id: 'p_1005', name: 'Merino Beanie', price: 19.0, sku: 'BEAN-MER-005', image: 'data:image/jpeg;base64,',
 },
    ]
    setTimeout(() => products.value.push(...mocked), 250)
  } catch (e) {
    console.error(e)
    show?.({ message: 'Failed to load products', error: true })
  } finally {
    loading.value = false
  }
}

function openCreateDialog () {
  dialog.value = { open: true, mode: 'create', editingId: null }
  resetForm()
}

function resetForm () {
  form.value = { name: '', price: null, image: '', sku: '' }
  formIsValid.value = false
}

function closeDialog () {
  dialog.value.open = false
}

async function saveProduct () {
  const result = await formRef.value?.validate?.()
  if (result && result.valid === false) return

  try {
    saving.value = true
    const newProduct = { id: 'p_' + Date.now(), ...form.value }
    products.value.unshift(newProduct)
    show?.({ message: 'Product created!' })
    closeDialog()
  } catch (e) {
    console.error('Save failed (mock)', e)
    show?.({ message: 'Save failed', error: true })
  } finally {
    saving.value = false
  }
}

function goToProduct (item) {
  router.push({ name: 'Product', query: { id: item.id } })
}

/* Utils */
function formatPrice (n) {
  if (n === null || n === undefined || n === '') return '—'
  try {
    return new Intl.NumberFormat(undefined, { style: 'currency', currency: 'USD' })
      .format(Number(n))
  } catch {
    return `$${n}`
  }
}
</script>

<style scoped>
