  border-radius: 12px;
  padding: 10px 10px 6px;
  background: rgba(255,255,255,0.4);
}

.table-title {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 6px 4px 8px;
}

.stack {
  display: flex;
  flex-direction: column;
  gap: 10px;
  max-height: calc(100vh - 180px);
  overflow: auto;
  padding-right: 2px;
}

/* compact, clickable row */
.prod-row {
  display: grid;
  grid-template-columns: 56px 1fr; /* no action buttons now */
  align-items: center;
  gap: 10px;
  padding: 8px;
  cursor: pointer;
  transition: background-color .15s ease, box-shadow .15s ease, transform .03s ease;
}

/* subtle “flash” highlight on hover; supports light & dark themes */
.prod-row:hover {
  background-color: rgba(0,0,0,0.06);
  box-shadow: var(--v-shadow-4);
}

/* dark theme tweak */
:deep(.v-theme--dark) .prod-row:hover {
  background-color: rgba(255,255,255,0.06);
}

/* tiny press feedback */
.prod-row:active {
  transform: translateY(1px);
}

.truncate {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
</style>
